require(["../config.min","../base.min"],
		function(){require(["common/modal.min"]),
	require(["common/alert.min"],
			function(n){$(".delete").on("click",function(){var drawsName=$(this).attr("data-id");
			n.confirm("您确定要删除吗？",
					function(){
				$.ajax({
						url:"materielDrawService/deleteMaterielDraws.do?type=add",
						type:"POST",
						data:{"id":drawsName},
						success:function(data){
							var json = eval(data); //数组  
							$("#drawsForm tr").empty("");
					               $.each(json, function (index, item) {  
					                   //循环获取数据      
					                   var serNo=(index+1);  
					               		$("#drawsForm").append("<tr><td>"+serNo+"</td><td>"+json[index].drawingName+"</td><td>"+json[index].version+"</td><td>"+json[index].reviser+"</td><td>"+json[index].revisedRecord+"</td><td><a class='delete' data-id="+json[index].drawingName+"><i class='iconfont icon-delete'></i></a></td> </tr>");
					               		location.reload();
					               });
								},
						error:function(){
							alert("删除失败");
						}
						})})})
	})
});