	$(function(){$("#statusSelect").on("change",function(){"1"==$(this).val()?$("#statusRelated").show():$("#statusRelated").hide()})
		if(state=='1'){//设置退市日期是否可见
		     $("#statusRelated").show()
		  }
		  //返回确认
		  $("#returnbtn").on("click",function(){
	        if(confirm("确定放弃编辑返回吗？")){
	           location.href=basePath+"instrumentService/queryInstrument.do?sapNo="+sapNo;
	        }
	     });

			$("#fileicon-pdf").attr("style", "display:none");
			$("#fileicon-word").attr("style", "display:none");
			$("#fileicon-picture").attr("style", "display:none");
			$(".deletFile").attr("style", "display:none");
			$(".deleteMpic").attr("style", "display:none");
			$(".right").attr("style", "display:none");
			$(".error").attr("style", "display:none");
			//仪器图片删除图标
			$(".deleteMpic").attr("style", "display:none");
			var subfix = test(executionStandard);
			if (subfix == '.pdf') {
				$("#fileicon-pdf").attr("style", "display:block");
				$("#fileicon-word").attr("style", "display:none");
			} else if (subfix == '.doc' || subfix == '.docx') {
				$("#fileicon-word").attr("style", "display:block");
				$("#fileicon-pdf").attr("style", "display:none");
			}
		
			$("[name=sapNo]").on("blur", function() {
				var sapNo = $(this).val();
				if (sapNo != '') {
					$.ajax({
						url : "instrumentService/querySapNo.do",
						type : "POST",
						data : {
							"sapNo" : sapNo
						},
						dataType : "json",
						success : function(data) {
							if (data.result == 'exitSapNo') {
								$(".error").attr("style", "display:block");
								$(".right").attr("style", "display:none");
								$(".sapNoMessage").html("该SAP已被占用");
							} else {
								$(".right").attr("style", "display:block");
								$(".error").attr("style", "display:none");
								$(".sapNoMessage").html("");
							}
						},
						error : function() {
							alert("操作失败");
							$(".right").attr("style", "display:none");
							$(".error").attr("style", "display:none");
						}
					});
				}
			});
		
	});
