mapper.select:
select com.*,ma.*,t.* from tbl_business_completemachinedetail com left join tbl_business_materiel ma on com.materielNo=ma.materielNo join tbl_business_materietype t on ma.materielTypeId=t.materielTypeId where com.compleId = #{completeId,jdbcType=INTEGER}
and ( com.materielNo like CONCAT('%',#{keys},'%') or ma.materielName like CONCAT('%',#{keys},'%') )
ORDER BY com.updateTime desc LIMIT #{curPage},#{pageSize}
