mapper.select:
select m.*,t.* from tbl_business_materiel m left join tbl_business_materietype t on m.materielTypeId=t.materielTypeId
where m.materielNo like CONCAT('%',#{keys},'%') or m.materielName like CONCAT('%',#{keys},'%') or t.materielTypeName like CONCAT('%',#{keys},'%')
ORDER BY m.updateTime desc LIMIT #{curPage},#{pageSize}

select m.* ,cird.*,t.* from tbl_business_circuitboarddetail cird join tbl_business_materiel m on cird.materielNo=m.materielNo join tbl_business_materietype t on m.materielTypeId=t.materielTypeId where cird.materielNo in
#{materielNo}
ORDER BY m.updateTime desc

mapper.insert:
insert into tbl_business_materiel (materielNo, materielName, materielTypeId, 
  modelSpecification, footprint, picture, updateTime,
  comment) values
  
  (#{materiel.materielNo}, #{materiel.materielName}, #{materiel.materielTypeId.materielTypeId}, #{materiel.modelSpecification},
   #{materiel.footprint}, #{materiel.picture},#{materiel.updateTime}, #{materiel.comment}
  )
